const dotenv = require('dotenv');
const express = require('express');
const bodyParser = require('body-parser'); // Import body-parser for JSON parsing
const swaggerUi = require('swagger-ui-express');
const swaggerDocument = require('../swagger.json'); 
const authRoutes = require('./routes/authRoutes');

// Load environment variables from .env file
dotenv.config();

const app = express();
const PORT = process.env.PORT || 3000;

// Middleware to parse JSON
app.use(bodyParser.json());



// Set up user routes
app.use('/api/v1', authRoutes);


// Swagger API documentation route
app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swaggerDocument));

// Start the server
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});

module.exports = app;

const { googleLogin } = require('../Services/authService');

const googleLoginController = async (req, res) => {
  const { token } = req.query;

  if (!token) {
    return res.status(400).json({
      status: 400,
      message: 'Authorization code is required',
    });
  }

  try {
    const userData = await googleLogin(token);

    return res.status(200).json({
      status: 200,
      message: 'Login successful',
      data: userData,
    });
  } catch (error) {
    // Add debug logging
    console.error('Controller error:', error);
    console.error('Status code:', error.statusCode);
    
    // Use the error's status code or default to 400
    const statusCode = error.statusCode || 400;
    
    return res.status(statusCode).json({
      status: statusCode,
      message: error.message || 'Something went wrong',
    });
  }
};

module.exports = { googleLoginController };

const { OAuth2Client } = require('google-auth-library');
const { User } = require('../../models'); // Adjust the path as needed
const jwt = require('jsonwebtoken');

const client = new OAuth2Client(process.env.GOOGLE_CLIENT_ID);

const googleLogin = async (token) => {
  try {
    // Verify Google token
    const ticket = await client.verifyIdToken({
      idToken: token,
      audience: process.env.GOOGLE_CLIENT_ID,
    });

    const payload = ticket.getPayload();
    const email = payload.email; // Use email to check if the user exists
    // const firstName = payload.given_name;
    // const lastName = payload.family_name;
    // const profilePicture = payload.picture;

    // Check if the user exists in the database using email
    let user = await User.findOne({ where: { email } });

    // If the user does not exist, create a new user
    if (!user) {
        const error = new Error('Account does not exist. Please create an account first.');
        error.statusCode = 404;
        throw error;
      }

    // Generate JWT token
    const authToken = jwt.sign({ userId: user.id, role: user.role }, process.env.JWT_SECRET, { expiresIn: '1h' });

    return {
      userId: user.id,
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      profilePicture: user.profilePicture,
      token: authToken,
    };
  } catch (error) {
    console.error('Error in googleLogin:', error);
    throw error;
  }
};

module.exports = { googleLogin };

'use strict';

const fs = require('fs');
const path = require('path');
const Sequelize = require('sequelize');
const basename = path.basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = require(__dirname + '/../config/config.js')[env];
const db = {};

// Initialize Sequelize
let sequelize;
if (config.use_env_variable) {
  sequelize = new Sequelize(process.env[config.use_env_variable], config);
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

// Load all models
fs.readdirSync(__dirname)
  .filter(file => {
    return (
      file.indexOf('.') !== 0 &&
      file !== basename &&
      file.slice(-3) === '.js' &&
      file.indexOf('.test.js') === -1
    );
  })
  .forEach(file => {
    const model = require(path.join(__dirname, file))(sequelize, Sequelize.DataTypes);
    db[model.name] = model;
  });

// Associate models
Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

module.exports = db;

