const dotenv = require('dotenv');
const express = require('express');
const bodyParser = require('body-parser'); // Import body-parser for JSON parsing
const swaggerUi = require('swagger-ui-express');
const swaggerDocument = require('../swagger.json'); // Assuming your swagger.json is located at this path
const userRoutes = require('./routes/userRoutes'); // Import user routes

dotenv.config();

const app = express();
const PORT = process.env.PORT || 3000;

app.use(bodyParser.json());

// Set up user routes
app.use('/api/v1', userRoutes); 

app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swaggerDocument));

// Start the server
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});

module.exports = app;

const express = require('express');
const { userRegistrationController } = require('../controllers/UserControllers/userController');

const router = express.Router();

router.post('/auth/register', userRegistrationController);

module.exports = router;

const { createUser } = require('../../Services/UserService');

const userRegistrationController = async (req, res) => {
  try {
    const userData = req.body;
    
    const { user, token } = await createUser(userData);
    return res.status(201).json({
      message: 'User successfully registered',
      user,
      token
    });
  } catch (err) {
    return res.status(400).json({
      message: err.message || 'An error occurred during registration'
    });
  }
};

module.exports = { userRegistrationController };

const { User } = require('../../models');
const { validateUserRegistration, encryptPassword, generateJwtToken } = require('../validations/userValidation');

const createUser = async (userData) => {
  await validateUserRegistration(userData);
  const hashedPassword = await encryptPassword(userData.password);

  const newUser = await User.create({
    firstName: userData.firstName,
    lastName: userData.lastName,
    email: userData.email,
    birthOfDate: userData.birthOfDate,
    preferredLanguage: userData.preferredLanguage,
    preferredCurrency: userData.preferredCurrency,
    whereYouLive: userData.whereYouLive,
    role: userData.role,
    department: userData.department,
    lineManager: userData.lineManager,
    gender: userData.gender,
    telephoneNumber: userData.telephoneNumber,
    password: hashedPassword
  });

  const token = generateJwtToken(newUser);
  return { user: newUser, token };
};

module.exports = { createUser };

'use strict';

const fs = require('fs');
const path = require('path');
const Sequelize = require('sequelize');
const process = require('process');
const basename = path.basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = require(__dirname + '/../config/config.js')[env];
const db = {};

let sequelize;
if (config.use_env_variable) {
  sequelize = new Sequelize(process.env[config.use_env_variable], config);
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

fs
  .readdirSync(__dirname)
  .filter(file => {
    return (
      file.indexOf('.') !== 0 &&
      file !== basename &&
      file.slice(-3) === '.js' &&
      file.indexOf('.test.js') === -1
    );
  })
  .forEach(file => {
    const model = require(path.join(__dirname, file))(sequelize, Sequelize.DataTypes);
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

module.exports = db;

